	GIV(needGCFlag) = 1;
	forceInterruptCheck();
	if (GIV(freeStart) <= GIV(reserveStart)) {
		GIV(freeStart) = newFreeStart;
		newObj = oopForPointer(newChunk1);
		goto l2;
	}
	newObj = 0;
