/*	Answer the number of bytes required to represent the value of a
	CSmallInteger. 
 */

	/* LargeIntegersPlugin>>#cDigitLengthOfCSI: */
static sqInt
cDigitLengthOfCSI(sqInt csi)
{
	if (csi >= 0) {
		if (csi < 256) {
			return 1;
		}
		if (csi < 65536) {
			return 2;
		}
		if (csi < 0x1000000) {
			return 3;
		}
		
#    if BytesPerOop == 4
		return 4;

#    else /* BytesPerOop == 4 */
		if (csi < 0x100000000ULL) {
			return 4;
		}
		if (csi < 0x10000000000ULL) {
			return 5;
		}
		if (csi < 0x1000000000000ULL) {
			return 6;
		}
		if (csi < 0x100000000000000ULL) {
			return 7;
		}
		return 8;

#    endif /* BytesPerOop == 4 */

	}
	if (csi > -256) {
		return 1;
	}
	if (csi > -65536) {
		return 2;
	}
	if (csi > -16777216) {
		return 3;
	}
	
#  if BytesPerOop == 4
	return 4;

#  else /* BytesPerOop == 4 */
	if (csi > -4294967296) {
		return 4;
	}
	if (csi > -1099511627776) {
		return 5;
	}
	if (csi > -281474976710656) {
		return 6;
	}
	if (csi > -72057594037927936) {
		return 7;
	}
	return 8;

#  endif /* BytesPerOop == 4 */

}

