{
    sqInt referent;

	assert(isUnambiguouslyForwarder(objOop));
	referent = longAt((objOop + BaseHeaderSize) + (0 << 2));
	while (((referent & 3) == 0)
	 && (((longAt(referent)) & 0x3FFFFF) == 8)) {
		referent = longAt((referent + BaseHeaderSize) + (0 << 2));
	}
	return referent;
}


