{
	
	switch (registerIndex) {
	case TempReg:
		return RAX;

	case ClassReg:
		return RCX;

	case ReceiverResultReg:
		return RDX;

	case SendNumArgsReg:
		return R9;

	case SPReg:
		return RSP;

	case FPReg:
		return RBP;

	case Arg0Reg:
		return RDI;

	case Arg1Reg:
		return RSI;

	case VarBaseReg:
		return RBX;

	case RISCTempReg:
		return R8;

	case Scratch0Reg:
		return R10;

	case Scratch1Reg:
		return R11;

	case Scratch2Reg:
		return R12;

	case Scratch3Reg:
		return R13;

	case Scratch4Reg:
		return R14;

	case Scratch5Reg:
		return R15;

	default:
		assert(((registerIndex >= RAX) && (registerIndex <= R15)));
		return registerIndex;

	}
}

