    sqInt rm;
    sqInt ro;

	modrm = byteAt(pc + 1);
	mod = ((usqInt) modrm) >> 6;
	ro = (((usqInt) modrm) >> 3) & 7;
	rm = modrm & 7;
	if (mod == 3) {
		return 2;
	}
	if (rm != 4) {

		/* no SIB byte */
		
		switch (mod) {
		case 0:
			return (rm == 5
				? 6
				: 3);

		case 1:
			return 3;

		case 2:
			return 6;

		default:
			error("Case not found and no otherwise clause");
			return -1;
		}
	}
