			assert(((limit - 24) & (BytesPerWord - 1)) == 0);
			GIV(memoryLimit) = limit - 24;
			initializeMemoryFirstFree(GIV(freeStart));
			sqMakeMemoryNotExecutableFromTo(startOfMemory(), GIV(memoryLimit));
