	/* first data word, 'does' */

	wholeWord = longAt((longAt((GIV(specialObjectsOop) + BaseHeaderSize) + (SelectorDoesNotUnderstand << ShiftForWord))) + BaseHeaderSize);
	return (imageFormatVersion()) | (wholeWord & 0xFF000000UL);
